const MakeTextPages = (textPage: string) => {
  const arrayText = textPage.split(';')
  let text: string[] = []
  for (let prop in arrayText) {
    text.push(`<text:p>${arrayText[prop]}</text:p>`)
  }
  let textReturn = text.join('<text:p/>')
  return textReturn
}


